using Prism.Commands;
using Prism.Mvvm;
using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using Xamarin.Forms;

namespace SelfCheckout.ViewModels
{
    public class TestBase64ImageViewModel : BindableBase
    {
        string _base64Image = "";
        public TestBase64ImageViewModel()
        {

        }

        public ImageSource Base64Image { get => ImageSource.FromStream(() => new MemoryStream(Convert.FromBase64String(_base64Image))); }
    }
}
